sys_script_include.script:
function DevToolsPrompt(strPrompt)
{
	Debug('DevToolsPrompt');
    if (strPrompt == '')
	{
        return false;
    }

	Debug(strPrompt);

    var credential = x_snc_devtools.GetCredentialFromAlias("x_snc_ad.OpenAI");
	var strToken = credential['apikey'];

	Debug(strToken);

	if (strToken == '')
	{
		return false;
	}

    var restMessage = new sn_ws.RESTMessageV2();
    restMessage.setHttpMethod('post');
    restMessage.setEndpoint('https://api.openai.com/v1/chat/completions');
    restMessage.setRequestHeader('Authorization', 'Bearer ' + strToken);
    restMessage.setRequestHeader('Content-Type', 'application/json');
    restMessage.setRequestBody(JSON.stringify({
		'model': 'gpt-4o',
        'messages': [
			{
				"role": "user",
				"content": strPrompt
			}],
        'max_tokens': 60
    }));

    var response = restMessage.execute();
    var responseBody = response.getBody();
    var httpStatus = response.getStatusCode();
    if (httpStatus != 200)
	{
        return false;
    }

	var parsedResponse = x_snc_devtools.ParseJson(responseBody);
	return x_snc_devtools.GetValue(parsedResponse,'choices',0,'message','content');
}
